//-----------------------------------------------------------------------------------------
// IMPORT STATEMENTS
//-----------------------------------------------------------------------------------------
import React, { Component } from 'react';
import Movie from './Movie';

//-----------------------------------------------------------------------------------------
// COMPONENT
//-----------------------------------------------------------------------------------------
class MovieList extends Component {

    constructor(){
        super();

        this.createURL = this.createURL.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    // FUNCTIONS
    createURL(imdbID){
        var newurl = 'https://www.imdb.com/title/'+imdbID+'/'
        return newurl
    }

    handleClick = (e) => {
        this.props.nominateMovie(e.target.value)
    }

    render(){
        return (
            <div className="movieBox">
            {this.props.data.movieList.map((movie) => {
                return(
                    <Movie key={movie.title} movie={movie} nominateMovie={this.props.nominateMovie} data={this.props.data}/>
                )     
            })}
            </div>
    )
  }
}

export default MovieList;
